import { Injectable } from '@angular/core';
import {HttpClient, HttpResponse} from "@angular/common/http";
import {Observable} from "rxjs";
import {Product} from "../model/product.model";

@Injectable({
  providedIn: 'root'
})
export class ProductService {

  constructor(private http: HttpClient) { }

  public getProducts(keyword :string="",page : number=1, size:number=10){
    return this.http.get(`http://localhost:3000/products? _page=${page}&_limit=${size}`,{observe:'response'});
  }
  public checkProduct(product:Product):Observable<Product>{
    return this.http.patch<Product>(`http://localhost:3000/products/${product.id}`,
      {checked:!product.checked});
  }
  public deleteProduct(product:Product){
    return this.http.delete<any>(`http://localhost:3000/products/${product.id}`);
  }

  saveProduct(product: Product):Observable<Product> {
    return this.http.post<Product>(`http://localhost:3000/products`,
      product);
  }

  // public searchProducts(keyword:string,page:number=1,size:number=3):Observable<Array<Product>>{
  //   return this.http.get<Array<Product>>(`http://localhost:3000/products?name_like=${keyword} & _page=${page} & _limit=${size}`);
  // }
}
